import React from "react";
import Container from "@material-ui/core/Container";
import Typography from "@material-ui/core/Typography";
import { makeStyles, Box } from "@material-ui/core";


import SummaryStats from "../components/SummaryStats";
import Spacer from "../components/Spacer";
import ProvinceStats from "../components/ProvinceStats";
import { Base } from "../components/Base";
import Doctor from'../assets/doctor.svg';
import StatsCharts from "../components/StatsCharts";

const useStyles = makeStyles({
    container: {
        backgroundColor: "#F5F5F5",
        minHeight: "100vh",
    },
    doctorIcon: {
        width: "70%"
    },
    link: {
        fontWeight: "bold",
        color: "#6C63FF",
        textDecoration: "none",
        marginLeft: 4
    }
});

const IndexPage = () => {
    const classes = useStyles();
    return (
        <Base>
            <Container maxWidth="sm" className={classes.container}>
                <Spacer variant="vertical">
                    <Typography component="h1" variant="h3" align="center">
                        Perkembangan COVID-19 di Indonesia
                    </Typography>
                </Spacer>
                <Box display="flex" justifyContent="center" my={2}>
                    <img className={classes.doctorIcon} src={Doctor} alt=""/>
                </Box>
                <Typography component="p" className={classes.noMargin}>
                    Update terakhir : <span className={classes.date}>{lastUpdate.toDateString().split(' ').slice(1).join(' ')}</span>.
                    <a className={classes.link} href="https://github.com/mathdroid/indonesia-covid-19-api/" target="_blank">Sumber Data</a>
                </Typography>
                <Spacer variant="vertical">
                    <SummaryStats />
                </Spacer>
                <Spacer variant="vertical"> 
                    <ProvinceStats />
                </Spacer>
                <Spacer variant="vertical">
                    <StatsCharts />
                </Spacer>
            </Container>
        </Base>
    );
};

export default IndexPage;
