import { useState, useEffect } from "react";
const useData = (url) => {
    const [data, setData] = useState();
    useEffect(() => {
        const fetchData = async () => {
            const result = await fetch(url)
                .then((response) => response.json())
                .catch((err) => err);
            setData(result);
        };
        fetchData();
    }, []);
    return data;
};

export default useData;
