import { useState, useEffect } from "react";
import { summaryUrl, areaUrl, dailyUrl } from "./url";

const fetchData = (url) => {
    const [data, setData] = useState();
    useEffect(() => {
        const fetchData = async () => {
            console.log("Fetching data...");
            const result = await fetch(url)
                .then((response) => response.json())
                .catch((err) => err);
            setData(result);
        };
        fetchData();
    }, []);
    return data;
};

const useSummaryStats = () => fetchData(summaryUrl);
const useProvinceStats = () => fetchData(areaUrl);
const useLastUpdate = async () => {
    const response = await fetchData(dailyUrl);

    console.log(response[response.length].tanggal);
};
export { useSummaryStats, useProvinceStats, useLastUpdate };
