import { summaryUrl } from "../utils/url";
import useData from "../utils/useData";
import StatsCard from "./StatsCard";
import { Grid } from "@material-ui/core";

const SummaryStats = () => {
    const stats = useData(summaryUrl);
    if (!stats) return "Fetching data please wait ...";
    return (
        <Grid container spacing={2}>
            <Grid item xs={6}>
                <StatsCard number={stats.jumlahKasus} information="Jumlah Kasus"/>
            </Grid>
            <Grid item xs={6}>
                <StatsCard number={stats.perawatan} information="Kasus Positif"/>
            </Grid>
            <Grid item xs={6}>
                <StatsCard number={stats.meninggal} information="Meninggal"/>
            </Grid>
            <Grid item xs={6}>
                <StatsCard number={stats.sembuh} information="Sembuh"/>
            </Grid>
        </Grid>
    );
};

export default SummaryStats;
