import React from 'react';
import { makeStyles, Typography } from '@material-ui/core';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import PropTypes from 'prop-types';

const useStyles = makeStyles({
    root: {
    //   minWidth: 275,
    },
    bold: {
        fontWeight: "bold"
    },
    importantCard: {
        borderLeft: "3px solid #E53935"
    },
    importantText: {
        color: "#E53935"
    },
    healthyText: {
        color: "#329932"   
    }
  });
const ProvinceCard = props => {
    const classes = useStyles();
    const { important, stat} = props;
    const importantCard = important ? classes.importantCard : null;
    const importantText = important ? classes.importantText : null;
    return (
        <Card className={`${classes.root} ${importantCard}`}>
            <CardContent>
                <Typography component="p" className={classes.bold}>
                    {stat.provinsi}
                </Typography>
                <Typography component="p" variant="subtitle2">
                    Positif: {stat.kasusPosi}
                </Typography>
                <Typography className={classes.healthyText} component="p" variant="subtitle2">
                    Sembuh: {stat.kasusSemb}
                </Typography>
                <Typography className={importantText} component="p" variant="subtitle2">
                    Meninggal: {stat.kasusMeni}
                </Typography>
            </CardContent>
        </Card>
    );
};

ProvinceCard.propTypes = {
    important: PropTypes.bool
};

export default ProvinceCard;
