import { summaryUrl } from "../utils/url";
import StatsCard from "./StatsCard";
import { Grid, Typography, makeStyles } from "@material-ui/core";
import { useSummaryStats, useLastUpdate } from "../utils/fetcher";

const useStyles = makeStyles({
    bold: {
        fontWeight: "bold"
    },
    date: {
        fontWeight: "bold",
        color: "#6C63FF"
    },
    noMargin: {
        margin: 0
    },
    link: {
        color: "#6C63FF",
        textDecoration: "none"
    }
})

const SummaryStats = () => {
    const classes = useStyles();
    const stats = useSummaryStats();
    const lastUpdate = useLastUpdate();
    if (!stats || !lastUpdate) return "Fetching data please wait ...";
    return (
        <Grid container spacing={2}>
            <Grid item xs={12}>
                <Typography component="h2" variant="h5" className={classes.bold}>
                    Kasus di Seluruh Indonesia
                </Typography>
            </Grid>
            <Grid item xs={6}>
                <StatsCard
                    number={stats.jumlahKasus}
                    information="Jumlah Kasus"
                />
            </Grid>
            <Grid item xs={6}>
                <StatsCard
                    number={stats.perawatan}
                    information="Kasus Positif"
                />
            </Grid>
            <Grid item xs={6}>
                <StatsCard number={stats.sembuh} information="Sembuh" />
            </Grid>
            <Grid item xs={6}>
                <StatsCard number={stats.meninggal} information="Meninggal" />
            </Grid>
            <Grid item xs={12}>
                <Typography component="p" className={classes.noMargin}>
                    Update terakhir : <span className={classes.date}>{lastUpdate.toDateString().split(' ').slice(1).join(' ')}</span>.
                    <a className={classes.link} href="https://github.com/mathdroid/indonesia-covid-19-api/" target="_blank">Sumber Data</a>
                </Typography>
            </Grid>
        </Grid>
    );
};

export default SummaryStats;
