import { useState, useEffect } from "react";
import { summaryUrl, areaUrl, dailyUrl } from "./url";

const fetchData = (url) => {
    const [data, setData] = useState();
    useEffect(() => {
        const fetchData = async () => {
            console.log("Fetching data...");
            const result = await fetch(url)
                .then((response) => response.json())
                .catch((err) => err);
            setData(result);
        };
        fetchData();
    }, []);
    return data;
};

const useSummaryStats = () => fetchData(summaryUrl);
const useProvinceStats = () => fetchData(areaUrl);
const useLastUpdate = () => {
    const response = fetchData(dailyUrl);
    return response ? new Date(response.data[response.data.length-1].tanggal) : "Fetching data ..."
};
export { useSummaryStats, useProvinceStats, useLastUpdate };
