import { useState, useEffect } from "react";
export default useData = (url) => {
    const [data, setData] = React.useState();
    useEffect(() => {
        const fetchData = async () => {
            const result = await fetch(url)
                .then((response) => response.json())
                .catch((err) => err);
            setData(result);
        }
        fetchData();
    }, []);
    return data;
}
