import React from "react";
import Spacer from "./Spacer";

import { Typography, makeStyles } from "@material-ui/core";
import GraphChart from "./GraphChart";
import { useDailyStats } from "../utils/fetcher";

const useStyles = makeStyles({
    bold: {
        fontWeight: "bold",
    },
});
const StatsCharts = () => {
    const classes = useStyles();
    const stats = useDailyStats();
    if (!stats) return "Fetching data please wait...";
    return (
        <Spacer variant="vertical">
            <Spacer variant="verticalBottom">
                <Typography
                    component="h2"
                    variant="h5"
                    className={classes.bold}
                >
                    Grafik Kumulatif COVID-19
                </Typography>
            </Spacer>
            <GraphChart data={stats.data.map(d => ({hariKe: d.hariKe, jumlahKasusKumulatif: d.jumlahKasusKumulatif}))} valueField="jumlahKasusKumulatif" argumentField="hariKe"/>
        </Spacer>
    );
};

export default StatsCharts;
