import React, { useState } from "react";
import { Typography, Grid } from "@material-ui/core";
import { areaUrl } from "../utils/url";
import useData from "../utils/useData";
import ProvinceCard from "./ProvinceCard";
import Spacer from "../components/Spacer";

const ProvinceStats = () => {
    const stats = useData(areaUrl);
    const [limit, setLimit] = useState(8);
    const handleChangeLimit = () => {
        setLimit(limit + 8);
    };
    if (!stats) return "Fetching data please wait ...";
    return (
        <div>
            <Spacer variant="vertical">
                <Typography component="h2" variant="h5">
                    Kasus Berdasarkan Provinsi
                </Typography>
            </Spacer>
            <Grid container spacing={2}>
                {stats.data.slice(0, limit).map((stat, index) =>
                    stat.kodeProve !== 0 ? (
                        <Grid item xs={6} key={index}>
                            <ProvinceCard important={index < 5} stat={stat} />
                        </Grid>                        
                    ) : null
                )}
            </Grid>
            {
                limit < stats.data.length
                ?
                <Spacer variant="vertical">
                    <Typography
                        component="p"
                        align="center"
                        onClick={handleChangeLimit}
                    >
                        Tampilkan lebih banyak
                    </Typography>
                </Spacer>
                :
                null
            }
        </div>
    );
};

export default ProvinceStats;
